mapper.select:
SELECT MAX(TOTAL_QUANTITY)
FROM IMPORTEXPORT
WHERE ITEM_NUM =#{item_num}

mapper.update:
UPDATE ITEM_MANAGEMENT
SET IMPORT_QUANTITY = #{quantity}
WHERE ITEM_NUM =#{updateNum}

UPDATE ITEM_MANAGEMENT
SET
EXPORT_QUANTITY = #{quantity},
QUANTITY = IMPORT_QUANTITY-#{quantity}
WHERE ITEM_NUM = #{item_num}
